// hooks/useCourts.ts
import { useState, useEffect } from 'react';
import { supabase } from '../app/integrations/supabase/client'; // Adjust the path as needed
import { Court } from '../types'; // Adjust the path as needed

const useCourts = () => {
  const [courts, setCourts] = useState<Court[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchCourts = async () => {
      setLoading(true);
      try {
        const { data: courtsData, error: courtsError } = await supabase
          .from('courts')
          .select('*');

        if (courtsError) {
          setError(courtsError.message);
          return;
        }

        // Fetch active check-ins to calculate active players and average skill level
        const { data: checkinsData, error: checkinsError } = await supabase
          .from('checkins')
          .select('*')
          .eq('active', true);

        if (checkinsError) {
          setError(checkinsError.message);
          return;
        }

        const courtsWithActivity = courtsData.map((court) => {
          const activePlayers = checkinsData.filter((checkin) => checkin.court_id === court.id);
          const activePlayersCount = activePlayers.length;

          let totalSkillLevel = 0;
          activePlayers.forEach((player) => {
            totalSkillLevel += player.skill_level; // Assuming skill_level is a number
          });

          const averageSkillLevel = activePlayersCount > 0 ? totalSkillLevel / activePlayersCount : 0;

          return {
            ...court,
            activePlayers: activePlayersCount,
            averageSkillLevel: averageSkillLevel,
          };
        });

        setCourts(courtsWithActivity);
      } catch (err: any) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    const setupRealtimeSubscription = () => {
      const checkinsSubscription = supabase
        .channel('public:checkins')
        .on(
          'postgres_changes',
          { event: '*', schema: 'public', table: 'checkins' },
          (payload) => {
            console.log('Change received!', payload);
            fetchCourts(); // Re-fetch courts when check-ins change
          }
        )
        .subscribe();

      return () => {
        supabase.removeChannel(checkinsSubscription);
      };
    };

    fetchCourts();
    const unsubscribe = setupRealtimeSubscription();

    return () => {
      unsubscribe();
    };
  }, []); // Removed setupRealtimeSubscription from dependency array and manually unsubscribed

  return { courts, loading, error };
};

export default useCourts;
```

