Deno.serve(async (req: Request) => {
  try {
    const payload: FriendRequestPayload = await req.json();
    console.log('Friend request notification triggered:', payload);

    // Initialize Supabase client with service role key for admin access
    const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    const { record } = payload;

    // Get requester (sender) details
    const { data: requester, error: requesterError } = await supabase
      .from('users')
      .select('email, phone')
      .eq('id', record.user_id)
      .single();

    if (requesterError) {
      console.error('Error fetching requester:', requesterError);
      throw requesterError;
    }

    // Get recipient details
    const { data: recipient, error: recipientError } = await supabase
      .from('users')
      .select('email, phone, push_token')
      .eq('id', record.friend_id)
      .single();

    if (recipientError) {
      console.error('Error fetching recipient:', recipientError);
      throw recipientError;
    }

    const requesterIdentifier = requester.email || requester.phone || 'Someone';
    const recipientEmail = recipient.email;

    // Send email notification if recipient has email
    if (recipientEmail) {
      console.log('Sending email to:', recipientEmail);

      // Use Supabase Auth's built-in email service
      // Note: This requires SMTP to be configured in Supabase dashboard
      const emailHtml = `
        <!DOCTYPE html>
        <html>
          <head>
            <meta charset="utf-8">
            <style>
              body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
              .container { max-width: 600px; margin: 0 auto; padding: 20px; }
              .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; border-radius: 10px 10px 0 0; }
              .content { background: #f9f9f9; padding: 30px; border-radius: 0 0 10px 10px; }
              .button { display: inline-block; background: #667eea; color: white; padding: 12px 30px; text-decoration: none; border-radius: 5px; margin-top: 20px; }
              .footer { text-align: center; margin-top: 20px; color: #666; font-size: 12px; }
            </style>
          </head>
          <body>
            <div class="container">
              <div class="header">
                <h1>🎾 New Friend Request!</h1>
              </div>
              <div class="content">
                <p>Hi there!</p>
                <p><strong>${requesterIdentifier}</strong> wants to connect with you on PickleRadar!</p>
                <p>Accept their friend request to see when they're playing at nearby courts and coordinate your pickleball sessions.</p>
                <a href="https://natively.dev" class="button">Open PickleRadar</a>
              </div>
              <div class="footer">
                <p>PickleRadar - Find your pickleball community</p>
              </div>
            </div>
          </body>
        </html>
      `;

      // Send email using fetch to a mail service
      // Since SMTP is configured, we can use Supabase's email service or an external service
      // For now, we'll use a simple fetch to demonstrate
      // In production, you'd use SendGrid, Resend, or similar

      // Option 1: Use Resend (recommended)
      const resendApiKey = Deno.env.get('RESEND_API_KEY');
      if (resendApiKey) {
        const emailResponse = await fetch('https://api.resend.com/emails', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${resendApiKey}`,
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            from: 'PickleRadar <noreply@natively.dev>',
            to: [recipientEmail],
            subject: `${requesterIdentifier} sent you a friend request on PickleRadar`,
            html: emailHtml,
          }),
        });

        if (!emailResponse.ok) {
          const errorText = await emailResponse.text();
          console.error('Failed to send email via Resend:', errorText);
        } else {
          console.log('Email sent successfully via Resend');
        }
      } else {
        console.log('RESEND_API_KEY not configured, skipping email');
      }
    }

    // Send push notification if recipient has a push token
    if (recipient.push_token) {
      console.log('Sending push notification to token:', recipient.push_token);

      // Send push notification using Expo Push Notification service
      const pushMessage = {
        to: recipient.push_token,
        sound: 'default',
        title: 'New Friend Request! 👋',
        body: `${requesterIdentifier} wants to connect with you on PickleRadar`,
        data: {
          type: 'friend_request',
          friendshipId: record.id,
          requesterId: record.user_id,
        },
      };

      const pushResponse = await fetch('https://exp.host/--/api/v2/push/send', {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(pushMessage),
      });

      if (!pushResponse.ok) {
        const errorText = await pushResponse.text();
        console.error('Failed to send push notification:', errorText);
      } else {
        const pushResult = await pushResponse.json();
        console.log('Push notification sent successfully:', pushResult);
      }
    }

    return new Response(
      JSON.stringify({ 
        success: true, 
        message: 'Notifications sent successfully',
        emailSent: !!recipientEmail,
        pushSent: !!recipient.push_token,
      }),
      {
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive',
        },
      }
    );
  } catch (error) {
    console.error('Error in notify-friend-request function:', error);
    return new Response(
      JSON.stringify({ 
        success: false, 
        error: error.message,
      }),
      {
        status: 500,
        headers: {
          'Content-Type': 'application/json',
          'Connection': 'keep-alive',
        },
      }
    );
  }
});

