import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

const supabaseUrl = Deno.env.get('SUPABASE_URL')!;
const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;

interface NotificationRequest {
  userId: string;
  title: string;
  body: string;
  type: 'friend_request' | 'friend_accepted' | 'friend_checkin' | 'friend_checkout' | 'checkin_confirmation' | 'auto_checkout';
  data?: Record<string, any>;
}

Deno.serve(async (req: Request) => {
  try {
    // Only allow POST requests
    if (req.method !== 'POST') {
      return new Response(
        JSON.stringify({ error: 'Method not allowed' }),
        { status: 405, headers: { 'Content-Type': 'application/json' } }
      );
    }

    const supabase = createClient(supabaseUrl, supabaseServiceKey);
    const payload: NotificationRequest = await req.json();

    console.log('Sending notification:', payload);

    // Validate required fields
    if (!payload.userId || !payload.title || !payload.body || !payload.type) {
      return new Response(
        JSON.stringify({ error: 'Missing required fields' }),
        { status: 400, headers: { 'Content-Type': 'application/json' } }
      );
    }

    // Insert notification into database
    const { data: notification, error: dbError } = await supabase
      .from('notifications')
      .insert([
        {
          user_id: payload.userId,
          title: payload.title,
          body: payload.body,
          type: payload.type,
          data: payload.data || {},
          read: false,
        },
      ])
      .select()
      .single();

    if (dbError) {
      console.error('Database error:', dbError);
      return new Response(
        JSON.stringify({ error: 'Failed to create notification', details: dbError }),
        { status: 500, headers: { 'Content-Type': 'application/json' } }
      );
    }

    // Get user's push token
    const { data: user, error: userError } = await supabase
      .from('users')
      .select('push_token, notifications_enabled')
      .eq('id', payload.userId)
      .single();

    if (userError) {
      console.error('Error fetching user:', userError);
      // Still return success since notification was saved to DB
      return new Response(
        JSON.stringify({ success: true, notification, pushSent: false }),
        { status: 200, headers: { 'Content-Type': 'application/json' } }
      );
    }

    // Send push notification if user has enabled notifications and has a push token
    let pushSent = false;
    if (user?.notifications_enabled && user?.push_token) {
      try {
        const expoPushResponse = await fetch('https://exp.host/--/api/v2/push/send', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
          },
          body: JSON.stringify({
            to: user.push_token,
            title: payload.title,
            body: payload.body,
            data: payload.data || {},
            sound: 'default',
            priority: 'high',
          }),
        });

        const expoPushResult = await expoPushResponse.json();
        console.log('Expo push result:', expoPushResult);
        pushSent = expoPushResponse.ok;
      } catch (pushError) {
        console.error('Error sending push notification:', pushError);
      }
    }

    return new Response(
      JSON.stringify({ success: true, notification, pushSent }),
      { status: 200, headers: { 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error in send-notification function:', error);
    return new Response(
      JSON.stringify({ error: 'Internal server error', details: error.message }),
      { status: 500, headers: { 'Content-Type': 'application/json' } }
    );
  }
});

