CREATE OR REPLACE FUNCTION notify_friends_on_checkin()
RETURNS TRIGGER AS $$
DECLARE
  friend_record RECORD;
  court_record RECORD;
  user_record RECORD;
BEGIN
  -- Get court name
  SELECT name INTO court_record FROM courts WHERE id = NEW.court_id;

  -- Get user info
  SELECT first_name, last_name, pickleballer_nickname INTO user_record FROM users WHERE id = NEW.user_id;

  -- Get all accepted friends of the user who checked in
  FOR friend_record IN 
    SELECT friend_id FROM friends 
    WHERE user_id = NEW.user_id AND status = 'accepted'
    UNION
    SELECT user_id FROM friends 
    WHERE friend_id = NEW.user_id AND status = 'accepted'
  LOOP
    -- Insert notification for each friend
    INSERT INTO notifications (user_id, title, body, type, data)
    VALUES (
      friend_record.friend_id,
      'Friend Checked In! 👋',
      COALESCE(user_record.first_name || ' ' || SUBSTRING(user_record.last_name, 1, 1) || '.', 'A friend') || 
      COALESCE(' (' || user_record.pickleballer_nickname || ')', '') || 
      ' has checked in at ' || COALESCE(court_record.name, 'a court'),
      'friend_checkin',
      jsonb_build_object(
        'court_id', NEW.court_id,
        'court_name', court_record.name,
        'user_id', NEW.user_id
      )
    );
  END LOOP;

  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

