    WHERE user_id = OLD.user_id AND status = 'accepted'
    UNION
    SELECT user_id FROM friends 
    WHERE friend_id = OLD.user_id AND status = 'accepted'
  LOOP
    -- Insert notification for each friend
    INSERT INTO notifications (user_id, title, body, type, data)
    VALUES (
      friend_record.friend_id,
      'Friend Checked Out 👋',
      COALESCE(user_record.first_name || ' ' || SUBSTRING(user_record.last_name, 1, 1) || '.', 'A friend') || 
      COALESCE(' (' || user_record.pickleballer_nickname || ')', '') || 
      ' has checked out of ' || COALESCE(court_record.name, 'a court'),
      'friend_checkout',
      jsonb_build_object(
        'court_id', OLD.court_id,
        'court_name', court_record.name,
        'user_id', OLD.user_id
      )
    );
  END LOOP;

  RETURN OLD;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Function to notify user on friend request
CREATE OR REPLACE FUNCTION notify_on_friend_request()
RETURNS TRIGGER AS $$
DECLARE
  requester_record RECORD;
BEGIN
  -- Only notify on new pending requests
  IF NEW.status = 'pending' THEN
    -- Get requester info
    SELECT first_name, last_name, pickleballer_nickname, email, phone 
    INTO requester_record 
    FROM users WHERE id = NEW.user_id;

    -- Insert notification for the friend being requested
    INSERT INTO notifications (user_id, title, body, type, data)
    VALUES (
      NEW.friend_id,
      'New Friend Request! 👋',
      COALESCE(requester_record.first_name || ' ' || SUBSTRING(requester_record.last_name, 1, 1) || '.', 
               requester_record.email, 
               requester_record.phone, 
               'Someone') || 
      COALESCE(' (' || requester_record.pickleballer_nickname || ')', '') || 
      ' wants to connect with you',
      'friend_request',
      jsonb_build_object(
        'friendship_id', NEW.id,
        'requester_id', NEW.user_id
      )
    );
  END IF;

  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Function to notify requester when friend request is accepted
CREATE OR REPLACE FUNCTION notify_on_friend_accept()
RETURNS TRIGGER AS $$
DECLARE
  accepter_record RECORD;
BEGIN
  -- Only notify when status changes from pending to accepted
  IF OLD.status = 'pending' AND NEW.status = 'accepted' THEN
    -- Get accepter info
    SELECT first_name, last_name, pickleballer_nickname, email, phone 
    INTO accepter_record 
    FROM users WHERE id = NEW.friend_id;

    -- Insert notification for the requester
    INSERT INTO notifications (user_id, title, body, type, data)
    VALUES (
      NEW.user_id,
      'Friend Request Accepted! 🎉',
      COALESCE(accepter_record.first_name || ' ' || SUBSTRING(accepter_record.last_name, 1, 1) || '.', 
               accepter_record.email, 
               accepter_record.phone, 
               'Your friend') || 
      COALESCE(' (' || accepter_record.pickleballer_nickname || ')', '') || 
      ' accepted your friend request',
      'friend_accepted',
      jsonb_build_object(
        'friendship_id', NEW.id,
        'friend_id', NEW.friend_id
      )
    );
  END IF;

  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

